module AbsCPP where

-- Haskell module generated by the BNF converter


newtype Id = Id String deriving (Eq,Ord,Show)
data Program =
   PDefs [Def]
  deriving (Eq,Ord,Show)

data Def =
   DFun Type Id [Arg] Body
 | DFunIn Type Id [Arg] Body
 | DType Type Id
 | DInit Type [Id] Exp
 | DDEc Type [Id]
 | DStruc Id [Decs]
 | DUsing [Name]
  deriving (Eq,Ord,Show)

data Decs =
   Dec Type [Id]
  deriving (Eq,Ord,Show)

data Body =
   EBody
 | Body [Stm]
  deriving (Eq,Ord,Show)

data Arg =
   TArg Type
 | IdArg Type Id
 | DArg Type Id Exp
 | CArg Arg
  deriving (Eq,Ord,Show)

data Stm =
   SExp Exp
 | SDecl Type [Id]
 | SInit Type [Id] Exp
 | SReturn Exp
 | SWhile Exp Stm
 | SDo Stm Exp
 | SFor Stm Exp Exp Stm
 | SIf Exp Stm
 | STypeD Type Id
  deriving (Eq,Ord,Show)

data Name =
   IdName Id
 | TypeName Type
  deriving (Eq,Ord,Show)

data Literal =
   IntL Integer
 | StringL [String]
 | CharL Char
 | FloatL Double
 | IdentL Id
  deriving (Eq,Ord,Show)

data Exp =
   ELiteral Literal
 | EQConst [Name]
 | EIndex Exp Exp
 | EFunc Exp [Exp]
 | EDot Exp Exp
 | EArrow Exp Exp
 | ELShift Exp Exp
 | ERShift Exp Exp
  deriving (Eq,Ord,Show)

data Type =
   TString
 | TInt
 | TDouble
 | TTemplate Id Type
 | TVoid
 | TBool Boole
  deriving (Eq,Ord,Show)

data Boole =
   BTrue
 | BFalse
  deriving (Eq,Ord,Show)

